import React, { useState, useEffect } from "react";
import {
  View,
  Text,
  FlatList,
  StyleSheet,
  TouchableOpacity,
} from "react-native";
import BASEAPIURL from "../vars";
import { useSelector } from "react-redux";

const Home = () => {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await fetch(`${BASEAPIURL}/tasks`);
        const tasksData = await response.json();

        if (response.ok) {
          setTasks(tasksData);
        } else {
          console.error("Failed to fetch tasks:", tasksData.message);
        }
      } catch (error) {
        console.error("An error occurred:", error);
      }
    };

    fetchTasks();
  }, []);

  const renderItem = ({ item }) => (
    <View style={styles.taskItem}>
      <Text>{item.title}</Text>
      {/* Render additional task details */}
    </View>
  );

  const handleAddTask = () => {
    // Implement the logic to navigate to the Add Task screen
    // You can use the navigation object from react-navigation to navigate to the desired screen
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Task List</Text>
      <TouchableOpacity style={styles.addButton} onPress={handleAddTask}>
        <Text style={styles.addButtonText}>Add Task</Text>
      </TouchableOpacity>
      {tasks.length === 0 ? (
        <Text>No tasks available</Text>
      ) : (
        <FlatList
          data={tasks}
          renderItem={renderItem}
          keyExtractor={(item) => item.id}
          contentContainerStyle={styles.taskList}
        />
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    marginBottom: 20,
  },
  addButton: {
    backgroundColor: "blue",
    padding: 10,
    borderRadius: 5,
    marginBottom: 10,
  },
  addButtonText: {
    color: "white",
    fontSize: 16,
    fontWeight: "bold",
  },
  taskList: {
    flexGrow: 1,
    width: "100%",
  },
  taskItem: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    padding: 10,
    marginBottom: 10,
    backgroundColor: "#eee",
    borderRadius: 5,
  },
});

export default Home;
